package week1.day1;

public class NumberPositiveOrNegativeAssign {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=-42;
		if(num<0)
		{
			System.out.println(num + "is a negative number");
		}
		else if(num>0){
			System.out.println(num + "is a positive number");
		}

	}

}
